import React, { useState } from "react";
import "./"

function ContentArea() {
  const [selectedTaskType, setSelectedTaskType] = useState("Đã hoàn thành");

  const handleTaskClick = (taskType) => {
    setSelectedTaskType(taskType);
  };

  return (
    <div className="content-box">
      <h2 className="greeting">
        Xin chào <span className="fetch-data">Đặng Minh Anh!</span>
      </h2>
      <div className="recent-tasks">
        <h3>Các nhiệm vụ gần đây</h3>
        <div className="task-boxes">
          <div className="completed-tasks drop-down" onClick={() => handleTaskClick("Đã hoàn thành")}>
            Đã hoàn thành
          </div>
          <div className="incompleted-tasks drop-down" onClick={() => handleTaskClick("Chưa hoàn thành")}>
            Chưa hoàn thành
          </div>
          <div className="outdated-tasks drop-down" onClick={() => handleTaskClick("Quá hạn")}>
            Quá hạn
          </div>
          <div className="dropdown-choosen-taskbox">
            {selectedTaskType === "Đã hoàn thành" && <p>Hiển thị tasks đã hoàn thành</p>}
            {selectedTaskType === "Chưa hoàn thành" && <p>Hiển thị tasks chưa hoàn thành</p>}
            {selectedTaskType === "Quá hạn" && <p>Hiển thị tasks quá hạn</p>}
          </div>
        </div>
      </div>
      <div className="recent-notifications">
        <div>
          <h3>Thông báo</h3>
          <span className="days-filter drop-down">5 ngày trước</span>
        </div>
        <div className="notify-table">
          {/* Các header: Mã môn, Môn, Lớp, Giảng Viên, Thông báo, Thời gian */}
        </div>
      </div>
    </div>
  );
}
