import React from "react";
import logoImage from "../assets/logo_image.jpg";
import { Bell, User } from "lucide-react";

function Header() {
  return (
    <div className="flex w-full bg-gray-100">
      <div className="flex items-center bg-white px-4 py-2 shadow-sm">
        <img src={logoImage} alt="Logo" className="w-[18px] h-[18px] object-contain" />
        <span className="ml-2 text-sm font-semibold text-red-700 leading-5">
          Sổ điện tử <br /> Sinh viên
        </span>
      </div>

      <div className="flex flex-1 items-center justify-between bg-indigo-500 text-white px-6">
        <h1 className="text-sm md:text-lg font-semibold tracking-wide text-center">
          TRƯỜNG ĐẠI HỌC MỸ THUẬT CÔNG NGHIỆP
        </h1>
        <div className="flex items-center gap-4">
          <Bell className="w-5 h-5" />
          <User className="w-5 h-5" />
        </div>
      </div>
    </div>
  );
}

export default Header;
