import React from "react";
import "./contentarea.css";
function ContentArea() {
  return (
    <div className="content-box">
        <h2 className="greeting">Xin chào <span className="fetch-data">Đặng Minh Anh!</span></h2>
        <div className="recent-tasks">
            <h3>Các nhiệm vụ gần đây</h3>
            <div className="task-boxes">
                <div className="completed-tasks drop-down">Đã hoàn thành</div>
                <div className="incompleted-tasks drop-down">Chưa hoàn thành</div>
                <div className="outdated-tasks drop-down">Quá hạn</div>
                {/* Hiển thị các tasks khi chọn một task box, mặc định để các task "đã hoàn thành" */}
                <div className="dropdown-choosen-taskbox">Các tasks</div> 
            </div>
        </div>
        <div className="recent-notifications">
            <div>
                <h3>Thông báo</h3>
                <span className="days-filter drop-down">5 ngày trước</span>
            </div>
            <div className="notify-table">
                {/*  */}
            </div>    
        </div>
    </div>
  );
}

export default ContentArea;
