import React from "react";
import logoImage from "../assets/logo_image.jpg";
import { Bell, User } from "lucide-react";

function Header() {
  return (
    <div className="container flex bg-gray-100">
      <div className="logo-banner flex items-center bg-white px-4 py-2 shadow-sm">
        <div className="logo_img">
          <img src={logoImage} alt="Logo" className="h-12 w-12 object-contain" />
        </div>
        <span className="ml-2 text-sm font-semibold text-red-700 leading-5">
          Sổ điện tử <br /> Sinh viên
        </span>
      </div>

      <div className="blue-banner flex flex-1 items-center justify-between bg-indigo-500 text-white px-6">
        <h1>TRƯỜNG ĐẠI HỌC MỸ THUẬT CÔNG NGHIỆP</h1>
        <div className="account-functions">
          <div className="notification"></div>
          <div className="user-account"></div>
        </div>
      </div>
    </div>
  );
}

export default Header;
